import { ToastrService } from 'ngx-toastr';
import { VoteService } from './../../services/vote/vote.service';
import { CandidateService } from './../../services/candidate/candidate.service';
import { PositionsService } from './../../services/positions/positions.service';
import { Router } from '@angular/router';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-vote',
  templateUrl: './vote.component.html',
  styleUrls: ['./vote.component.scss', './vote.component.css']
})
export class VoteComponent implements OnInit {


  samplePositions: any[] =
    [
      {
        'position': ' SchoolPresident',
        'candidates': [
          {
            'username': 'Manasseh Bundi',
            'image': 'data:image/jpeg;base64,'
          },
          {
            'username': 'Abraham Gitonga',
            'image': 'assets/abu2.jpg'
          },
          {
            'username': 'Juma Anderson',
            'image': 'data:image/jpeg;base64,'

          }
        ]

      },
      {
        'position': 'VicePresident',
        'candidates': [
          {
            'username': 'James Gichuru',
            'image': 'assets/abu2.jpg'
          },
          {
            'username': 'Elizabeth Wangari',
            'image': 'data:image/jpeg;base64,'

          },
          {
            'username': 'Mike Munde',
            'image': 'assets/abu2.jpg'
          }
        ]

      },
      {
        'position': 'Captain',
        'candidates': [
          {
            'username': 'Obina',
            'image': 'data:image/jpeg;base64,'

          },
          {
            'username': 'Priings',
            'image': 'assets/abu2.jpg'
          },
          {
            'username': 'Juma Anderson',
            'image': 'data:image/jpeg;base64,'

          }
        ]

      },


    ]
  positions:any = []

  currentPos: any[] = []
  currentPosition = 0
  selectedArray = []
  lastItem = false;
  positionsList: any[] = [];
  constructor(
    private router: Router,
    private positionservice: PositionsService,
    private candidateservice: CandidateService,
    private voteservice: VoteService,
    private toastrservice: ToastrService
  ) { }
  ngOnInit() {

    this.currentPos = this.positions[0]

    console.log(this.currentPos);
    this.positionservice.getAllPositions().subscribe((positions: any) => {
      // this.positionsList = positions.positions
      if (positions) {
        for (let i = 0; i < positions.positions.length; i++) {
          this.positionsList.push({
            'position': positions['positions'][i]['name'],
            'candidates': []
          })
        }
        console.log(this.positionsList);

        console.log(positions['positions'][3]);

      }
    })
    this.candidateservice.allcandidates().subscribe((candidates: any) => {
      let candidate = candidates.candidate;
      if (candidates.candidate) {
        for (let i = 0; i < candidate.length; i++) {
          let pos = this.positionsList.findIndex(pos => {
            return pos.position === candidate[i]['position']['name']
          })
          if (pos != -1) {
            console.log(this.positionsList)
            this.positionsList[pos]['candidates'].push({
              'id': candidate[i]['user']._id,
              'username': candidate[i]['user'].username,
              'image': candidate[i]['user'].imageUrl
            })
          }

        }
        this.positions = this.positionsList
        console.log(this.positions.length);
        console.log(this.positions);

        if (this.positions.length == 1 ) {
          this.lastItem = true
        }
      }
    })



  }

  next() {

    this.currentPosition += 1
    console.log(this.positions.length);
    console.log(this.currentPosition);

    if (this.currentPosition == this.positions.length - 1) {
      // this.router.navigate(['home'])
      // alert(this.selectedArray)
      this.lastItem = true
    }
    if (this.currentPosition >= this.positions.length) {
      this.currentPosition -= 1
      this.voteservice.vote(this.selectedArray).subscribe((x: any) => {
        alert('here')
        if (x) {

          this.router.navigate(['vote-success'])
          console.log('addded');


          let token = JSON.parse(localStorage.getItem('token'));
          token['votingStatus'] = true;
          localStorage.setItem('token', JSON.stringify(token))
          localStorage.setItem('votechosen', JSON.stringify(this.selectedArray))


        }
      }, error => {
        console.log(error);
        this.router.navigate(['home'])
        alert(error.error)
        this.toastrservice.error(error.error.message)

      })
    }
    console.log(this.currentPosition);


  }
  previous() {
    this.currentPosition -= 1
    if (this.currentPosition <= 0) {
      this.currentPosition = 0
    }
    console.log(this.currentPosition);
  }
  selectedCandidate(position, selectedCandidate) {


    let leelee = this.selectedArray.map(item => { return item['position'] }).indexOf(position)

    if (leelee >= 0) {
      this.selectedArray.splice(leelee, 1)
      this.selectedArray.push(
        {
          'position': position,
          'candidate': selectedCandidate
        }
      )
    } else {
      this.selectedArray.push(
        {
          'position': position,
          'candidate': selectedCandidate
        }
      )
    }
    for (let i = 0; i <= this.selectedArray.length; i++) {
      console.log(this.selectedArray[i]);

    }

  }
}